mapper.select:
select 

from tb_drug d,tb_drugtype dt
where d.drugtype_id = dt.id and d.id = #{id,jdbcType=INTEGER}

select 

from tb_drug d,tb_drugtype dt
where d.drugtype_id = dt.id and d.drug_no = #{drugNo,jdbcType=VARCHAR}

select 

from tb_drug d,tb_drugtype dt
where d.drugtype_id = dt.id and d.drug_name = #{drugName,jdbcType=VARCHAR}

mapper.insert:
insert into tb_drug (id, drugtype_id, drug_name, 
  drug_no, purpose, spec, 
  unit, howuse, produced_time, 
  valid_time, create_time, update_time, purchase_price, sale_price, remark
  )
values (#{id,jdbcType=INTEGER}, #{drugtypeId,jdbcType=INTEGER}, #{drugName,jdbcType=VARCHAR}, 
  #{drugNo,jdbcType=VARCHAR}, #{purpose,jdbcType=VARCHAR}, #{spec,jdbcType=VARCHAR}, 
  #{unit,jdbcType=CHAR}, #{howuse,jdbcType=VARCHAR}, str_to_date(#{producedTime},"%Y-%m-%d"), 
  str_to_date(#{validTime},"%Y-%m-%d"), now(), now(), #{purchasePrice,jdbcType=DECIMAL}, #{salePrice,jdbcType=DECIMAL}, #{remark,jdbcType=VARCHAR}
  )
