mapper.update:
update tb_stock
set stock_quantity = stock_quantity - #{stockQuantity,jdbcType=INTEGER}, update_time = now()
where drug_id = #{drugId,jdbcType=INTEGER} and batch_no = #{batchNo,jdbcType=VARCHAR}

update tb_stock

  
    update_time = now(),
  
  
    valid_waring = #{validWaring,jdbcType=VARCHAR},
  

where drug_id = #{drugId,jdbcType=INTEGER}
AND batch_no = #{batchNo,jdbcType=VARCHAR}

mapper.select:
  	select
  	id,warehouse_no,batch_no,drug_id,sum(stock_quantity) as stock_quantity,min_quantity,max_quantity
  	from tb_stock s
  	
		
			and s.drug_id = #{drugId}
		
  	
  	group by drug_id
  	order by s.id limit #{pageIndex} ,#{pageSize}

select 

from tb_stock
where drug_id = #{drugId,jdbcType=INTEGER} and batch_no = #{batchNo,jdbcType=INTEGER}
